B = (2,2);
C = (0,2);
D = A+dis*dir(d1);
G = O+dis*dir(d1);
E = B+dis*dir(d1);
F = C+dis*dir(d1);
G = O+dis*dir(d1);
X = (3,0);
Z = (0,3);
Y = O+2*dis*dir(d1);

fill(C--B--E--F--cycle,gray(0.8));
fill(O--E--F--C--cycle,gray(0.8));
draw(O--A--B--C--cycle);
draw(G--D, dashed);
draw(E--F);
draw(F--G, dashed);
draw(C--F);
draw(B--E);
draw(A--D);
draw(D--E);
draw(O--G, dashed);
draw(O--X, Arrow);
draw(O--Z, Arrow);
draw(O--E, red+dashed);
draw(C--B--E--F--cycle, red);
draw(O--B, red);
draw(O--F, red+dashed);
draw(O--Y, dashed, Arrow);

label("$2$", A, S);
label("$2$", C, W);
label("$2$", G, NW);
