pair D = (A+B)/2;
pair E = (C+A)/2;
pair F = (B+C)/2;

pair DH = D + (.5,0);
pair EH = E + (-.5,0);
pair DV = D + (0,.5);
pair FV = F + (0,-.5);

draw(A--B--C--cycle);
draw(DV--FV,dashed);
fill(D--F--C--A--cycle,gray(.7));

